"use strict";function mapShopsWithCategories(){let e=document.querySelectorAll(".shop-items > .category-items"),t={};for(let o of e)t[o.id]=o;return t}const SHOP_CATEGORIES_MAP=mapShopsWithCategories();function showProductsOfCategory(e){for(let t in SHOP_CATEGORIES_MAP)t===e?SHOP_CATEGORIES_MAP[t].style.display="block":SHOP_CATEGORIES_MAP[t].style.display="none"}let shopCategoriesParent=document.querySelectorAll(".shop-items-container > .shop-categories > .shop-categories-wrapper > ul");shopCategoriesParent.forEach(e=>{e.addEventListener("click",e=>{let t=String(e.target.innerHTML);t.indexOf("<span>")>=0&&(t=e.target.childNodes[0].innerHTML),t&&-1==t.indexOf("<")&&-1==t.indexOf(">")&&showProductsOfCategory(t)})});