"use strict";class ItemDIVs{constructor(){this.parents=document.querySelectorAll("#sidebar-items-div"),this.categoriesShown=!1,this.items=document.querySelectorAll(".sidebar-items-div")}forceHide(){this.categoriesShown=!1,this.shopsShown=!1,this._itemsDisplay()}itemsDisplay(e){-1!=e.indexOf("categories")&&(this.categoriesShown=!this.categoriesShown),this._itemsDisplay()}_itemsDisplay(){if(this.categoriesShown)for(let e of this.items[0].children)e.classList.remove("hide");else for(let s of this.items[0].children)s.classList.add("hide")}}const itemDIVsObj=new ItemDIVs,sidebarDropdownLinks=document.querySelectorAll(".sidebar-dropdown-link");sidebarDropdownLinks.forEach(e=>{e.addEventListener("click",function(){itemDIVsObj.itemsDisplay(e.id)})});const sidebarCloseButton=document.querySelector("#close-main-sidebar-btn");sidebarCloseButton.addEventListener("click",function(){itemDIVsObj.forceHide()});